/** @jsxImportSource @emotion/react */

import { Route, Routes, NavLink } from 'react-router-dom';
import DisplayItem from './Components/DisplayItem';
import { Global, css, jsx } from '@emotion/react';
import ShoppingCart from './Components/ShoppingCart'
import {React, useState, useEffect} from 'react';
import useProducts from './hooks/useProducts';
import { useDispatch } from 'react-redux';
import { receive_product } from './Redux/action';

const globalStyle = css`
  @import url('https://fonts.googleapis.com/css2?family=Redressed&display=swap');
    body{
      font-family: 'Redressed', cursive;
      margin: 0;
    }
`;



function App() {

  const h1Style = css`
    color: snow;
    display: flex;
    justify-content: center;
    border: 5px solid lightPink;
  `

  const {products, isLoading} = useProducts();
  const dispatch = useDispatch();

  useEffect(()=>{
    if(products)
      products.map((products)=> dispatch(receive_product(products))) 
  }, [products]);



  return (
    <div>
    <Global styles={globalStyle}/>
      <h1>PENNY CANDY STORE</h1>
      <Menu/>
      <Routes>
        <Route path='/' element={<Home/>}/>
        <Route path='/DisplayItem' element={<DisplayItem/>}/>
        <Route path='/ShoppingCart' element={<ShoppingCart/>}/>
        <Route path='/*' element={<Error/>}/>
      </Routes>      
    </div>
  );
}

function Home(){

  return(
    <div>
      Welcome to Penny Candy Store!
    </div>
  )

}

function Menu(){
  return(
    <div>
      <NavLink to='/'>Home</NavLink>
      <NavLink to='/DisplayItem'>Our Products</NavLink>
      <NavLink to='/ShoppingCart'>Checkout</NavLink>
    </div>
  );

}

function Error(){
  return(
    <div>
      <p>This page does not exist, please hit back or choose from the above options</p>
    </div>
  )
}



export default App;
