import {useState, useEffect, react} from 'react';
import {get_quantity, adj_quantity} from '../Redux/action';
import { useDispatch } from 'react-redux';



export function Counter(id){

    const [quantity, setQuantity] = useState('');
    const dispatch = useDispatch();

    return(
      <form
        onSubmit={(e)=>{
          e.preventDefault();
          dispatch(get_quantity(Number(quantity), id));
          dispatch(adj_quantity(Number(quantity), id));
        }}
      >
        <fieldset>
          <input value={quantity} onChange = {(e)=>setQuantity(e.target.value)} placeholder = 'Enter Quantity Here'></input>
          <button type="Submit">Add to Cart</button>
        </fieldset>
      </form>
    )

  }
