import { combineReducers } from 'redux';
import { RECEIVE_PRODUCT, RECEIVE_PRODUCT_STATUS, CHECK_OUT, GET_QUANTITY, ADJ_QUANTITY } from "./action";

const initialState = {
    products:[],
    cart: [], 
    currentItem: null,
}
let cartID = -1;

function candyStoreReducer(state = initialState, action){

    switch(action.type){
        case RECEIVE_PRODUCT:
            return{
                products:
                [...state.products,
                {
                   name: action.name,
                   id: action.id,
                   price: action.price,
                   inStock: action.inStock,
                   photoUrl: action.photoUrl
                }
            ]}
        case GET_QUANTITY:
            return {
                ...state,
                products: state.products.map((item)=>(
                    item.id === action.id.id ? {
                        ...item,
                        inStock: item.inStock - action.quantity,
                    } : item
                ))
            }

        default:
            return state;
    }
}

function shoppingCartReducer(state = initialState, action){
    switch(action.type){
        case ADJ_QUANTITY:
            return {
                cart: 
                [
                    console.log(state),
                    ...state.cart,
                    {
                        id: action.id.id,
                        name: action.id.name,
                        price: action.id.price,
                        quantity: action.quantity,
                        img: action.id.img
                    }
                ]
            }
        default:
            return state;
    }

}

const rootReducer = combineReducers({
    candyStore: candyStoreReducer,  
    cartStore: shoppingCartReducer,
})

export default rootReducer;
