/** @jsxImportSource @emotion/react */

import {React, useState, useEffect} from 'react';
import useProducts from '../hooks/useProducts';
import { useDispatch, connect } from 'react-redux';
import { receive_product } from '../Redux/action';
import { useSelector } from 'react-redux';
import { css } from '@emotion/react';


function DisplayItems({product}){

    const cardStyle = css`
      width: 36vw;
      height: 30vh;
      border: 5px solid lightskyblue;
      border-radius: 20px;
      display: flex;
      margin: 0;
      flex-wrap: wrap;
      flex-direction: column;
      align-items: center;

    `
    const bodyStyle = css`
      display: flex;
      flex-wrap: wrap;
      flex-direction: row;
      justify-content: space-evenly;

    `  

    const {products, isLoading} = useProducts();
    const dispatch = useDispatch();

    console.log(products);

    useEffect(()=>{
      if(products)
        products.map((products)=> dispatch(receive_product(products))) 
    }, [products]);


    return(
      <div css={bodyStyle}>
        {product.map(products=>
        <div key={products.id} css={cardStyle}>
          <h1>{products.name}</h1>
          <p>Price per candy: ${products.price}</p>
          <p>Stock: {products.inStock}</p>
          <img src={products.photoUrl} alt='penny candy' width='150px' height='150px'></img>
        </div>
        )}
      </div>
    )
  }

  const productArray = (state) => {
    return{
      product: state.candyStore.products
    }
  }

  export default connect(productArray)(DisplayItems)
