import { combineReducers } from 'redux';
import { RECEIVE_PRODUCT, RECEIVE_PRODUCT_STATUS } from "./action";


function candyStoreReducer(state = [], action){

    switch(action.type){
        case RECEIVE_PRODUCT:
            return[
                ...state,
                {
                   name: action.name,
                   id: action.id,
                   price: action.price,
                   inStock: action.inStock,
                   photoUrl: action.photoUrl
                }
            ]
        case RECEIVE_MAP:
            return state.map
        default:
            return state;
    }

}

const rootReducer = combineReducers({
    candyStore: candyStoreReducer,  
})

export default rootReducer;
