/** @jsxImportSource @emotion/react */

import React, { useState, useEffect } from 'react';
import useProducts from './hooks/useProducts';
import { useDispatch, connect } from 'react-redux';
import { Route, Routes, NavLink } from 'react-router-dom';
import {adj_quantity, get_quantity, receive_product} from './Redux/action';
import store from './Redux/store';
import {useSelector} from 'react-redux';
import {getProducts} from './Redux/selector';
import { css } from '@emotion/react';



function App() {

  return (
    <div>
      <Menu/>
      <Routes>
        <Route path='/' element={<Home/>}/>
        <Route path='/DisplayItem' element={<DisplayItems/>}/>
        <Route path='/ShoppingCart' element={<ShoppingCart/>}/>
      </Routes>      
    </div>
  );
}

function Home(){

  return(
    <div>
      Welcome to Penny Candy Store!
    </div>
  )

}

function Menu(){
  return(
    <div>
      <NavLink to='/'>Home</NavLink>
      <NavLink to='/DisplayItem'>Our Products</NavLink>
      <NavLink to='/ShoppingCart'>Checkout</NavLink>
    </div>
  );

}

function DisplayItems(){

  const cardStyle = css`
    width: 36vw;
    height: 30vh;
    border: 5px solid lightskyblue;
    border-radius: 20px;
    display: flex;
    margin: 0;
    flex-wrap: wrap;
    flex-direction: column;
    align-items: center;

  `
  const bodyStyle = css`
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    justify-content: space-evenly;

  `  

  const {products, isLoading} = useProducts();
  const dispatch = useDispatch();
  connect(productArray)


  useEffect(()=>{
    if(products)
      products.map((products)=> dispatch(receive_product(products))) 
  }, [products]);

  const productArray = state => {
    return{
      products: state.candyStore.products
    }
  }


  return(
    <div css={bodyStyle}>
      {productArray.map(products=>
      <div key={products.id} css={cardStyle}>
        <h1>{products.name}</h1>
        <p>Price per candy: ${products.price}</p>
        <p>Stock: {products.inStock}</p>
        <Counter id={products.id}/>
        <img src={products.photoUrl} alt='penny candy' width='150px' height='150px'></img>
      </div>
      )}
    </div>
  )
}

function Counter(id){

  const [quantity, setQuantity] = useState('');
  const dispatch = useDispatch();

  return(
    <form
      onSubmit={(e)=>{
        e.preventDefault();
        dispatch(get_quantity(quantity,id));
        dispatch(adj_quantity(quantity,id));
      }}
    >
      <fieldset>
        <input value={quantity} onChange = {(e)=>setQuantity(e.target.value)} placeholder = 'Quantity Here'></input>
        <button type="Submit">Add to Cart</button>
      </fieldset>
    </form>
  )

}


function ShoppingCart(){
  return(
    <div>
    </div>
  )
}


export default App;
