/** @jsxImportSource @emotion/react */

import {connect} from "react-redux"
import { RemoveButton } from "./RemoveButton"
import { css, jsx } from "@emotion/react"

export function ShoppingCart({cart}){
    const h1Style = css`
        font-size: 50px;
        color: MediumOrchid;
        border: 10px solid indigo;
        border-radius: 10px;
        background-color: LightPink;
        display: flex;
        align-items: center;
        justify-content: center;
        width: 30%;
        flex-direction: column;
        margin-left: 33%;

    `

    const cardStyle = css`
        background-color: LightPink;
        border-radius: 10px;
        border: 10px solid indigo;
        color: MediumOrchid;
        display: flex;
        justify-content: center;
        flex-direction: column;
        width: 40%;
        margin-left: 30%;

    `

    return(
      <div>
        <h1 css={h1Style} >Cart</h1>
        {cart.map((carts)=>
            <div css={cardStyle} key={carts.id}>
                <h1>{carts.name}</h1>
                <p>Quantity: {carts.quantity}</p>
                <p>Total: ${carts.quantity * carts.price} </p>
                <img src={carts.img} alt='Penny Candy' width='150px' height='150px'></img>
                <RemoveButton id={carts.id} quantity={carts.quantity}/>
            </div>
        )}
      </div>
    )
}

const cartArray =(state)=>{
    return{
        cart: state.cartStore.cart,
    }
}

export default connect(cartArray)(ShoppingCart)
