import { bindActionCreators, combineReducers } from "redux";
import { RECEIVE_PRODUCT, RECEIVE_PRODUCT_STATUS } from "./action";

const state = [];


export default function candyStoreReducer(state = [], action){

    switch(action.type){
        case RECEIVE_PRODUCT:
            return[
                ...state,
                {
                    action: action.array,
                }
            ]
        default:
            return state;
    }

}
